<?phpinclude 'include/config.php';header('Content-Type: application/json');$villed = mysqli_real_escape_string($link, $_GET['villed']);$villem = mysqli_real_escape_string($link, $_GET['villem']);// Essayer d'abord villed ? villem$sql = "SELECT fraistr, fxcar FROM trpub WHERE villed = ? AND villem = ?";$stmt = mysqli_prepare($link, $sql);mysqli_stmt_bind_param($stmt, "ss", $villed, $villem);mysqli_stmt_execute($stmt);$result = mysqli_stmt_get_result($stmt);$data = mysqli_fetch_assoc($result);// Si non trouv, essayer l'inverseif(!$data) {    $sql = "SELECT fraistr, fxcar FROM trpub WHERE villed = ? AND villem = ?";    $stmt = mysqli_prepare($link, $sql);    mysqli_stmt_bind_param($stmt, "ss", $villem, $villed);    mysqli_stmt_execute($stmt);    $result = mysqli_stmt_get_result($stmt);    $data = mysqli_fetch_assoc($result);}if($data) {    echo json_encode([        'success' => true,        'fraistr' => $data['fraistr'],        'fxcar' => $data['fxcar']    ]);} else {    echo json_encode([        'success' => false,        'message' => 'Aucun tarif trouv pour ce trajet'    ]);}mysqli_close($link);?>