<?phpinclude 'include/config.php';header('Content-Type: application/json');// Rcuprer toutes les villes uniques$sql = "SELECT villed AS ville FROM trpub UNION SELECT villem AS ville FROM trpub";$result = mysqli_query($link, $sql);if($result) {    $villes = [];    while($row = mysqli_fetch_assoc($result)) {        $villes[] = $row['ville'];    }    echo json_encode(['success' => true, 'villes' => $villes]);} else {    echo json_encode([        'success' => false,        'message' => 'Erreur de requte: ' . mysqli_error($link)    ]);}mysqli_close($link);?>