<?phpheader('Content-Type: application/json');// Configuration de la base de donnes$host = 'localhost';$db   = 'omcttunijorani';$user = 'root';$pass = '';$charset = 'utf8mb4';$dsn = "mysql:host=$host;dbname=$db;charset=$charset";$options = [    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,    PDO::ATTR_EMULATE_PREPARES   => false,];try {    $pdo = new PDO($dsn, $user, $pass, $options);        $villed = $_GET['villed'] ?? '';    $villem = $_GET['villem'] ?? '';        if(empty($villed) || empty($villem)) {        throw new Exception("Villes de dpart et d'arrive requises");    }        // Essayer d'abord la combinaison villed ? villem    $stmt = $pdo->prepare("SELECT fraistr, fxcar FROM trpub WHERE villed = ? AND villem = ? LIMIT 1");    $stmt->execute([$villed, $villem]);    $result = $stmt->fetch();        // Si non trouv, essayer l'inverse (villem ? villed)    if(!$result) {        $stmt = $pdo->prepare("SELECT fraistr, fxcar FROM trpub WHERE villed = ? AND villem = ? LIMIT 1");        $stmt->execute([$villem, $villed]);        $result = $stmt->fetch();    }        if($result) {        echo json_encode([            'success' => true,            'fraistr' => $result['fraistr'],            'fxcar' => $result['fxcar']        ]);    } else {        throw new Exception("Aucun tarif trouv pour ce trajet");    }    } catch (Exception $e) {    echo json_encode([        'success' => false,        'message' => $e->getMessage()    ]);}?>