<?phpheader('Content-Type: application/json');// Configuration de la base de donnes$host = 'localhost';$db   = 'omcttunijorani';$user = 'root';$pass = '';$charset = 'utf8mb4';$dsn = "mysql:host=$host;dbname=$db;charset=$charset";$options = [    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,    PDO::ATTR_EMULATE_PREPARES   => false,];try {    $pdo = new PDO($dsn, $user, $pass, $options);        // Rcuprer toutes les villes uniques (depart + arrivee)    $stmt1 = $pdo->query("SELECT villed FROM trpub UNION SELECT villem FROM trpub");    $villes = $stmt1->fetchAll(PDO::FETCH_COLUMN);        echo json_encode([        'success' => true,        'villes' => $villes    ]);    } catch (PDOException $e) {    echo json_encode([        'success' => false,        'message' => 'Erreur de connexion: ' . $e->getMessage()    ]);}?>